cross(v) {
  return new Vec3(
    this.y * v.z - this.z * v.y,
    this.z * v.x - this.x * v.z,
    this.x * v.y - this.y * v.x
  )
}

const aCrossB = a.cross(b);
console.log("a x b       =", aCrossB.print());
console.log("a . (a x b) =", a.dot(aCrossB));
console.log("b . (a x b) =", b.dot(aCrossB));

